import math

def is_quadratic_residue(n, p):
    if n % p == 0:
        return True
    return pow(n, (p-1)//2, p) == 1

def tonelli_shanks(n, p):
    if n % p == 0:
        return 0

    if not is_quadratic_residue(n, p):
        print("This value of n is not a quadratic residue")
        return None
    else:
        print("This value of n is a quadratic residue")
    if p % 4 == 3:
        return pow(n, (p+1)//4, p)
    Q = p-1
    S = 0
    while Q % 2 == 0:
        S += 1
        Q //= 2
    print("Q= ", Q)
    print("S= ", S)
    z = 2
    while is_quadratic_residue(z, p):
        z += 1
    print("z= ", z)
    M = S
    c = pow(z, Q, p)
    t = pow(n, Q, p)
    R = pow(n, (Q+1)//2, p)
    print("M= ", M)
    print("c= ", c)
    print("t= ", t)
    print("R= ", R)
    while t != 1:
        print("Loop")
        i = 0
        temp = t
        while temp != 1:
            i += 1
            temp = (temp*temp) % p
        print("i= ", i)
        pow2 = 2 ** (M-i-1)
        b = pow(c, pow2, p)
        M = i
        c = (b*b) % p
        t = (t*b*b) % p
        R = (R*b) % p
        print("b= ", b)
        print("M= ", M)
        print("c= ", c)
        print("t= ", t)
        print("R= ", R)
    return R

